import React from 'react'

export class DemoContainer extends React.Component {

  constructor() {
     super();
     this.state = {
       demoCall: []
     }
   }

   componentDidMount(){
     fetch("/api/v1/albums/1/")
     .then(resp => resp.json())
     .then(a => {
       this.setState({
         demoCall: a
       })
     })
     .catch(error => console.log(error))
   }

  render() {
    return (
      <div>
        <div className="row">
          <div className="demo-container text-center col-md-12">
            <h2>Try it Out!</h2><br /> <br />
          </div>
          <div className="row api-input">
            <div className="col-md-7 api-call">
              <div className="input-group mb-3">
                <div className="input-group-prepend">
                  <span className="input-group-text" id="basic-addon3">https://amy-winehouse-api.com/api/v1/</span>
                </div>
                <input type="text" className="form-control" id="basic-url" aria-describedby="basic-addon3" />
                  <div className="input-group-append">
                    <button className="btn btn-info" type="button">Submit</button>
                  </div>
              </div>
              <p>Open API endpoints:
                 <button type="button" class="btn btn-link">/artist</button>
                 <button type="button" class="btn btn-link">/albums/2</button>
                 <button type="button" class="btn btn-link">/artists/1/albums/3/</button>
                 <button type="button" class="btn btn-link">/quotes</button>
             </p>
             <div className="form-group">
             <label for="exampleFormControlTextarea1"><b>Resource</b></label>
             <textarea className="form-control" id="exampleFormControlTextarea1" value={this.state.demoCall} />
           </div>
              </div>
          </div>
      </div>
      </div>
  )
}

}
export default DemoContainer;
