import React from 'react'

export class APIInformation extends React.Component {

  constructor() {
     super();
     this.state = {
       artist: []
     }
   }

   componentDidMount(){
     fetch("/api/v1/artists/1/")
     .then(resp => resp.json())
     .then(a => {
       this.setState({
         artist: a
       })
     })
     .catch(error => console.log(error))
   }

  render() {
    return (
      <div>
      <div className="row api-information">
      <div className="col-md-6 offset-1">
          <h1>Welcome to the Amy Winehouse API Portal!</h1><br />
          <p>This API is able to make public API calls to retrieve certain information about this artist. <a href="#">Sign Up</a> for an API
          key and start making requests!
          </p>
          <center>
            {this.state.artist.map(art =>
              <div className="card">
              <img className="card-img-top" src={art.press_photo} />
            </div>
           )
               }
          </center>
      </div>
      </div>
      </div>
  )
}

}
export default APIInformation;
