import React from 'react'
import AlbumInfo from '../components/AlbumInfo'

export class AlbumInfoContainer extends React.Component {

  constructor(props) {
     super(props);
     this.state = {
       album: []
     };
   }

  componentDidMount(){
    fetch("/api/v1/artists/1/albums/1")
    .then(resp => resp.json())
    .then(a => {
      this.setState({
        album: a
      })
    })
    .catch(error => console.log(error))
  }

  handleClick = () => {
    alert("hi");
  }

  render() {
    let album = this.state.album;
    return (
      <div>
        <button type="button" className="btn btn-primary" onClick={this.handleClick}>Primary</button>
      </div>
  )
}

}
export default AlbumInfoContainer;
