'use strict';

var os = require('os');

var fs = require('fs');

var isWsl = function isWsl() {
  if (process.platform !== 'linux') {
    return false;
  }

  if (os.release().includes('Microsoft')) {
    return true;
  }

  try {
    return fs.readFileSync('/proc/version', 'utf8').includes('Microsoft');
  } catch (err) {
    return false;
  }
};

if (process.env.__IS_WSL_TEST__) {
  module.exports = isWsl;
} else {
  module.exports = isWsl();
}
