'use strict';

var MurmurHash3 = require('imurmurhash');

module.exports = function (uniq) {
  if (uniq) {
    var hash = new MurmurHash3(uniq);
    return ('00000000' + hash.result().toString(16)).substr(-8);
  } else {
    return (Math.random().toString(16) + '0000000').substr(2, 8);
  }
};
