import React from 'react'
import AlbumInfo from '../components/AlbumInfo'

export class AlbumInfoContainer extends React.Component {

  constructor(props) {
     super(props);
     this.state = {
       album: []
     };
   }

  componentDidMount(){
    fetch("/api/v1/artists/1/albums")
    .then(resp => resp.json())
    .then(a => {
      this.setState({
        album: a
      })
    })
    .catch(error => console.log(error))
  }

  getSongs = (id) => {
    fetch("/api/v1/artists/albums"+id)
  }

  render() {
    let albums = this.state.album;
    return (
      <div>
        {albums.map(a =>
          <div class="card" style="width: 18rem;">
          <img class="card-img-top" src={a.album_cover} />
          <div class="card-body">
            <p class="card-text">{a.name}</p>
          </div>
        </div>
       )
           }
      </div>
  )
}

}
export default AlbumInfoContainer;
